        if (_showValuesBox)
            ToggleValuesBox();

        Field.FormInputType = InputType;
        await Service.InsertFormFieldAsync(Field);
        FieldsList.Add(Field);
        ToggleCreateModal();

        this.StateHasChanged();
    }

    protected async void RemoveFormField(string id)
    {

